CREATE OR REPLACE FUNCTION obter_desc_status_pa (nr_seq_pa_status_p bigint, ie_opcao_p text default 'D') RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(80);


BEGIN
if (nr_seq_pa_status_p IS NOT NULL AND nr_seq_pa_status_p::text <> '') then

	select	max(ds_status)
	into STRICT	ds_retorno_w
	from	pa_status_paciente
	where	nr_sequencia = nr_seq_pa_status_p;

else

	if (ie_opcao_p = 'C') then
	
		select	max(nr_sequencia)
        into STRICT	ds_retorno_w
        from	pa_status_paciente
        where	IE_STATUS_INICIAL = 'S';
	
	
	else

        select	max(ds_status)
        into STRICT	ds_retorno_w
        from	pa_status_paciente
        where	IE_STATUS_INICIAL = 'S';
		
	end if;

end if;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
