CREATE OR REPLACE FUNCTION obter_desc_suep ( nr_seq_suep_p bigint, ie_opcao_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255) := '';



BEGIN

if (nr_seq_suep_p > 0) then

	select	max(DS_TITULO)
	into STRICT	ds_retorno_w
	from	suep
	where	nr_sequencia = nr_seq_suep_p;

	if (ie_opcao_p = 'A') then


		ds_retorno_w := ' ('||ds_retorno_w||')';


	end if;

end if;


return	ds_retorno_w;

end;
$body$
