CREATE OR REPLACE FUNCTION obter_desc_tabela_comp_param ( nr_seq_tabela_p bigint, nr_seq_tabela_param_p bigint, cd_estabelecimento_p bigint, cd_tabela_parametro_p bigint, cd_comp_parametro_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_tabela_param_p IS NOT NULL AND nr_seq_tabela_param_p::text <> '') then
	begin
	select	max(ds_tabela_custo)
	into STRICT	ds_retorno_w
	from	tabela_custo
	where	nr_sequencia	= nr_seq_tabela_param_p;
	END;
elsif (cd_tabela_parametro_p IS NOT NULL AND cd_tabela_parametro_p::text <> '') then
	begin
	select	ds_tabela_custo
	into STRICT	ds_retorno_w
	from	tabela_custo
	where	cd_tabela_custo		= cd_tabela_parametro_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
	end;
end if;

if (cd_comp_parametro_p IS NOT NULL AND cd_comp_parametro_p::text <> '') then
	begin
	select	ds_componente
	into STRICT	ds_retorno_w
	from	componente_custo
	where	cd_componente		= cd_comp_parametro_p
	and	cd_estabelecimento	= cd_estabelecimento_p;
	end;
end if;

return ds_retorno_w;

end;
$body$
