CREATE OR REPLACE FUNCTION obter_desc_tabela ( nm_tabela_p text) RETURNS varchar AS $body$
DECLARE


nm_tabela_w		varchar(255);


BEGIN

if (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') then
	begin
	select	substr(coalesce(obter_desc_expressao(cd_exp_cadastro,ds_cadastro), ds_tabela),1,255)
	into STRICT	nm_tabela_w
	from	tabela_sistema
	where 	nm_tabela = nm_tabela_p;
	end;
end if;

return	nm_tabela_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
