CREATE OR REPLACE FUNCTION obter_desc_tab_mat (cd_tab_preco_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(100);


BEGIN

if (cd_tab_preco_material_p IS NOT NULL AND cd_tab_preco_material_p::text <> '') then

	begin
	select	max(ds_tab_preco_mat)
	into STRICT	ds_retorno_w
	from	tabela_preco_material
	where	cd_tab_preco_mat 	= cd_tab_preco_material_p
	and	ie_hosp_pls = 'H';
	exception
		when others then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(308107); -- Sem pre√ßo informado
	end;

end if;

return	ds_retorno_w;

end;
