SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_termo (nr_sequencia_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_emissor_termo_w	varchar(10);
ie_emissor_termo_w	varchar(1);


BEGIN

if (coalesce(nr_sequencia_p,0) > 0) and (coalesce(nr_atendimento_p,0) > 0) then

	select	ie_emissor_termo
	into STRICT	ie_emissor_termo_w
	from	atend_dados_retirada
	where 	nr_atendimento = nr_atendimento_p
	and	nr_sequencia = nr_sequencia_p;

	if (ie_emissor_termo_w = 'S') then
		ds_emissor_termo_w := 'SVO';
	elsif (ie_emissor_termo_w = 'I') then
		ds_emissor_termo_w := 'IML';
	end if;
end if;

return	ds_emissor_termo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
