CREATE OR REPLACE FUNCTION obter_desc_tipo_assunto ( nr_seq_assunto_p bigint) RETURNS varchar AS $body$
DECLARE


ds_assunto_w	varchar(80);


BEGIN

if (nr_seq_assunto_p IS NOT NULL AND nr_seq_assunto_p::text <> '') then

	select  max(ds_assunto)
	into STRICT	ds_assunto_w
	from 	TIPO_ASSUNTO_RELAC_PF
	where 	nr_Sequencia = nr_Seq_assunto_p;

end if;

return	ds_assunto_w;

end;
