CREATE OR REPLACE FUNCTION obter_desc_tipo_aval_cih (nr_seq_tipo_aval bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(255);


BEGIN

if (nr_seq_tipo_aval IS NOT NULL AND nr_seq_tipo_aval::text <> '') then

	SELECT 	MAX(DS_TIPO_AVALIACAO)
	into STRICT	ds_retorno_w
	FROM 	TIPO_AVALIACAO_ANTIBIOTICO
	WHERE	ie_situacao = 'A'
	AND	nr_sequencia = nr_seq_tipo_aval;

end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
