CREATE OR REPLACE FUNCTION obter_desc_tipo_cobr (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			tipo_cobr_escrit.ds_tipo%type;


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	select	max(a.ds_tipo)
	into STRICT	ds_retorno_w
	from	tipo_cobr_escrit a
	where	a.nr_sequencia = nr_sequencia_p;

end if;

return	substr(ds_retorno_w,1,254);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
