CREATE OR REPLACE FUNCTION obter_desc_tipo_doc_item ( nr_seq_tipo_doc_item_p bigint) RETURNS varchar AS $body$
DECLARE


ds_descricao_w			varchar(100);


BEGIN
if (nr_seq_tipo_doc_item_p IS NOT NULL AND nr_seq_tipo_doc_item_p::text <> '') then
	select 	max(ds_tipo_item)
	into STRICT 	ds_descricao_w
	from 	tipo_doc_item
	where 	nr_sequencia = nr_seq_tipo_doc_item_p;

end if;

return ds_descricao_w;

end;
