CREATE OR REPLACE FUNCTION obter_desc_tipo_hemocomp ( nr_prescricao_p bigint, nr_seq_prescr_p bigint) RETURNS varchar AS $body$
DECLARE


ie_irradiado_w		varchar(1);
ie_lavado_w		varchar(1);
ie_filtrado_w		varchar(1);
ie_aliquotado_w		varchar(1);
ds_retorno_w		varchar(255);


BEGIN

select	coalesce(max(ie_aliquotado),'N'),
	coalesce(max(ie_filtrado),'N'),
	coalesce(max(ie_irradiado),'N'),
	coalesce(max(ie_lavado),'N')
into STRICT	ie_aliquotado_w,
	ie_filtrado_w,
	ie_irradiado_w,
	ie_lavado_w
from	prescr_procedimento
where	nr_prescricao	= nr_prescricao_p
and	nr_sequencia	= nr_seq_prescr_p;

if (ie_aliquotado_w 	= 'S') then
	ds_retorno_w 	:= wheb_mensagem_pck.get_texto(308135); -- Aliquotado
end if;
if (ie_filtrado_w 	= 'S') then
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(308136); -- Filtrado
	else
		ds_retorno_w	:= ds_retorno_w || ' / ' || wheb_mensagem_pck.get_texto(308136); -- Filtrado
	end if;
end if;
if (ie_irradiado_w	 = 'S') then
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(308138); -- Irradiado
	else
		ds_retorno_w	:= ds_retorno_w || ' / ' || wheb_mensagem_pck.get_texto(308138); -- Irradiado
	end if;
end if;
if (ie_lavado_w 	 = 'S') then
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= wheb_mensagem_pck.get_texto(308139); -- Lavado
	else
		ds_retorno_w	:= ds_retorno_w || ' / ' || wheb_mensagem_pck.get_texto(308139); -- Lavado
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
