CREATE OR REPLACE FUNCTION obter_desc_tipo_hist_saude (nr_seq_tipo_hist_p bigint, nr_seq_problema_p bigint default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(80);


BEGIN

if (nr_seq_tipo_hist_p IS NOT NULL AND nr_seq_tipo_hist_p::text <> '') then

	select	max(DS_TIPO_HISTORICO)
	into STRICT	ds_retorno_w
	from	tipo_historico_saude b
	where	b.nr_sequencia = nr_seq_tipo_hist_p;

elsif (nr_seq_problema_p IS NOT NULL AND nr_seq_problema_p::text <> '') then
	select	max(ds) ds
	into STRICT	ds_retorno_w
	from (
		SELECT	substr(obter_desc_problema(ie_tipo_registro, ie_tipo_problema, nm_tabela),1,200) ds
		from	lista_problema_pac_item
		where	nr_seq_problema = nr_seq_problema_p
		and	ie_tipo_registro = 'HS'
		order	by dt_atualizacao asc
	) alias4 LIMIT 1;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
