CREATE OR REPLACE FUNCTION obter_desc_tipo_item_atend (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_observacao_w	varchar(255);
nr_sequencia_w	bigint;

BEGIN

if (coalesce(nr_atendimento_p,0) >0 ) then

	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_sequencia_w
	from 	atend_tipo_item_dado
	where	nr_atendimento = nr_atendimento_p;

	if (coalesce(nr_sequencia_w,0) > 0) then

		select	ds_observacao
		into STRICT	ds_observacao_w
		from	atend_tipo_item_dado
		where	nr_sequencia = nr_sequencia_w;

	end if;

end if;

return	ds_observacao_w;

end;
$body$
LANGUAGE PLPGSQL
