CREATE OR REPLACE FUNCTION obter_desc_tipo_paciente (ie_tipo_paciente_p text) RETURNS varchar AS $body$
DECLARE


ds_tipo_paciente_w	    varchar(10);


BEGIN

if	ie_tipo_paciente_p = 'CI' then
		ds_tipo_paciente_w := wheb_mensagem_pck.get_texto(308140); -- Cirúrgico
elsif   ie_tipo_paciente_p = 'CL' then
		ds_tipo_paciente_w := wheb_mensagem_pck.get_texto(308141); -- Clínico
end if;

return	ds_tipo_paciente_w;

end;
