CREATE OR REPLACE FUNCTION obter_desc_tipo_pj (CD_TIPO_PESSOA_P bigint, IE_OPCAO_P text) RETURNS varchar AS $body$
DECLARE


cd_tipo_pj_w			smallint;
ds_tipo_pj_w			varchar(254);
ds_retorno_w			varchar(255);

/* IE_OPCAO_P

'C' - CODIGO
'D' - DESCRICAO
*/
BEGIN

select	cd_tipo_pessoa,
	ds_tipo_pessoa
into STRICT	cd_tipo_pj_w,
	ds_tipo_pj_w
from	tipo_pessoa_juridica
where	cd_tipo_pessoa = cd_tipo_pessoa_p;

if (ie_opcao_p = 'C') then
	ds_retorno_w := cd_tipo_pj_w;
elsif (ie_opcao_p = 'D') then
	ds_retorno_w := ds_tipo_pj_w;
end if;
return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
