CREATE OR REPLACE FUNCTION obter_desc_tipo_residuo (nr_seq_tipo_residuo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (nr_seq_tipo_residuo_p IS NOT NULL AND nr_seq_tipo_residuo_p::text <> '') then
	begin
		select  substr(NM_COMERCIAL,1,255) ds_tipo_residuo
		into STRICT	ds_retorno_w
		from    RESIDUO_NOME_COMERCIAL
		where   nr_sequencia = nr_seq_tipo_residuo_p;
	end;
else
	select  substr(obter_desc_expressao(327119),1,255) ds_tipo_residuo
	into STRICT	ds_retorno_w
	;
end if;



return	ds_retorno_w;

end;
$body$
