CREATE OR REPLACE FUNCTION obter_desc_tipo_saida ( ie_tiss_tipo_guia_p text, ie_tipo_saida_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (ie_tiss_tipo_guia_p = 5) then
	select	substr(cd_motivo || ' - ' || ds_motivo,1,255)
	into STRICT	ds_retorno_w
	from	tiss_motivo_saida_int
	where	nr_sequencia = ie_tipo_saida_p;
elsif (ie_tiss_tipo_guia_p = 4) then
	select	substr(cd_tipo_saida || ' - ' || ds_tipo_saida,1,255)
	into STRICT	ds_retorno_w
	from 	tiss_tipo_saida_spsadt
	where   nr_sequencia = ie_tipo_saida_p;
elsif (ie_tiss_tipo_guia_p = 3) then
	select	substr(cd_tipo_saida || ' - ' || ds_tipo_saida,1,255)
	into STRICT	ds_retorno_w
	from 	tiss_tipo_saida_consulta
	where   nr_sequencia = ie_tipo_saida_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
