CREATE OR REPLACE FUNCTION obter_desc_tipo_sol (ie_tipo_sol_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

if (ie_tipo_sol_p = '1') then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(308142); -- Solução intermitente
elsif (ie_tipo_sol_p = '2') then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(308143); -- Solução em esquema alternado
elsif (ie_tipo_sol_p = '3') then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(308144); -- Solução contínua
elsif (ie_tipo_sol_p = '4') then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(308146); -- Solução especial
elsif (ie_tipo_sol_p = '5') then
	ds_retorno_w	:= wheb_mensagem_pck.get_texto(308147); -- Solução para analgesia controlada pelo paciente (PCA)
end if;

return ds_retorno_w;

end;
