CREATE OR REPLACE FUNCTION obter_desc_tp_bolsa_coleta ( vl_dominio_p bigint, ie_tipo_descr_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(110);

/*
ie_tipo_bolsa - san_doacao
B - tipo de bolsa
C - tipo de coleta
*/
BEGIN

if (ie_tipo_descr_p = 'B') then
	begin
	select 	DS_VALOR_DOMINIO
	into STRICT	ds_retorno_w
	from	VALOR_DOMINIO
	where	cd_dominio = 2176
	and	vl_dominio =  vl_dominio_p;
	end;
elsif (ie_tipo_descr_p  = 'C') then
	begin
	select 	DS_VALOR_DOMINIO
	into STRICT	ds_retorno_w
	from	VALOR_DOMINIO
	where	cd_dominio = 1353
	and	vl_dominio =  vl_dominio_p;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
