CREATE OR REPLACE FUNCTION obter_desc_tratamento_agenda ( nr_sequencia_p bigint ) RETURNS varchar AS $body$
DECLARE

    ds_tratamento_w varchar(100) := null;

BEGIN
    if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then 
        select substr(rxt_obter_desc_tipo(c.nr_seq_tipo),1,40)
        into STRICT ds_tratamento_w
        from rxt_agenda a, rxt_tratamento b, rxt_tumor c
        where a.nr_seq_tratamento = b.nr_sequencia
        and b.nr_seq_tumor = c.nr_sequencia 
        and  a.nr_sequencia = nr_sequencia_p;

    end if;

    return ds_tratamento_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
