CREATE OR REPLACE FUNCTION obter_desc_tributo ( cd_tributo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tributo_w				varchar(40);


BEGIN

if (cd_tributo_p IS NOT NULL AND cd_tributo_p::text <> '') then

	select	coalesce(max(ds_tributo),'')
	into STRICT	ds_tributo_w
	from	tributo
	where	cd_tributo = cd_tributo_p;

end if;

return	ds_tributo_w;

END;
