CREATE OR REPLACE FUNCTION obter_desc_ult_def_inf ( nr_prescricao_p bigint, nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


nr_sequencia_w	bigint;
ds_definicao_w	varchar(255);


BEGIN

select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_sequencia_w
from 	cih_def_infect
where	nr_prescricao	= nr_prescricao_p
and	nr_seq_prescr 	= nr_sequencia_p;

if (nr_sequencia_w > 0) then
	select	max(obter_valor_dominio(2988,ie_definicao))
	into STRICT	ds_definicao_w
	from	cih_def_infect
	where	nr_sequencia = nr_sequencia_w;
end if;

return	ds_definicao_w;

end;
