SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_desc_unid_orcamentaria ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie_opcao_p
D - Descrição
C - Código unidade
CD - Código + descrição*/
ds_unidade_w	varchar(255);
cd_unidade_w	varchar(255);
ds_resultado_w	varchar(255);


BEGIN

select	ds_unidade,
	cd_unidade
into STRICT	ds_unidade_w,
	cd_unidade_w
from	unidade_orcamentaria
where	nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'D') then
	ds_resultado_w := ds_unidade_w;
elsif (ie_opcao_p = 'C') then
	ds_resultado_w := cd_unidade_w;
elsif (ie_opcao_p = 'CD') then
	ds_resultado_w := cd_unidade_w || ' - ' || ds_unidade_w;
end if;

return	ds_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
