CREATE OR REPLACE FUNCTION obter_desc_usuario_retir_devol ( nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(15) := null;


BEGIN

if (coalesce(nr_prescricao_p,0) > 0) and (coalesce(nr_seq_prescricao_p,0) > 0) then


	if (ie_opcao_p = 'R') then -- Retirada
		select max(nm_usuario)
		into STRICT   ds_retorno_w
		from   prescr_material_controle
		where  nr_prescricao 	 = nr_prescricao_p
		and    nr_seq_prescricao = nr_seq_prescricao_p
		and    ie_acao = 'R';


	elsif (ie_opcao_p = 'D') then -- Devolução
		select max(nm_usuario)
		into STRICT   ds_retorno_w
		from   prescr_material_controle
		where  nr_prescricao 	 = nr_prescricao_p
		and    nr_seq_prescricao = nr_seq_prescricao_p
		and    ie_acao = 'D';


	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
