CREATE OR REPLACE FUNCTION obter_desc_validacao_assinat (nr_seq_assinatura_p bigint) RETURNS varchar AS $body$
DECLARE


ds_xml_w varchar(32000);
ie_evento_w varchar(20);
ds_retorno_w varchar(50);


BEGIN
	select ds_xml, ie_evento
	into STRICT ds_xml_w, ie_evento_w
	from (SELECT	CONVERT_LONG_TO_VARCHAR2('DS_XML', 'TASY_ASSINAT_DIG_LOG', 'NR_SEQUENCIA = ' || NR_SEQUENCIA) ds_xml, ie_evento
		FROM	tasy_assinat_dig_log
		WHERE	nr_seq_assinatura = nr_seq_assinatura_p
		ORDER	BY dt_evento desc, nr_sequencia desc) alias1 LIMIT 1;

	if (ie_evento_w = 'Validado') then
		ds_retorno_w := obter_desc_expressao(786368);
	else
		if (position('PHILIPSINVALIDSIGNATUREEXCEPTION' in Upper(ds_xml_w)) > 0 or
		    position('-725' in Upper(ds_xml_w)) > 0 or position('-307' in Upper(ds_xml_w)) > 0 or position('-102' in Upper(ds_xml_w)) > 0) then
			ds_retorno_w := obter_desc_expressao(786372);
		else
			ds_retorno_w := obter_desc_expressao(786370);
		end if;
	end if;

	return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
