CREATE OR REPLACE FUNCTION obter_desc_visita_admissao (nr_sequencia_p bigint, nr_seq_tipo_admissao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tipo_admissao_w tipo_admissao_fat.ds_tipo_admissao%TYPE;


BEGIN
    if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
        begin
            SELECT b.ds_tipo_admissao ds
            INTO STRICT    ds_tipo_admissao_w
            FROM    subtipo_episodio a, tipo_admissao_fat b
            WHERE   a.nr_seq_tipo_admissao = b.nr_sequencia
            AND     b.ie_situacao = 'A'
            AND     a.nr_seq_tipo_admissao = nr_seq_tipo_admissao_p
            AND     a.nr_seq_tipo_episodio = (SELECT tpep.nr_sequencia
                                              from   tipo_episodio tpep
                                              where  tpep.nr_sequencia = a.nr_seq_tipo_episodio
                                              and    tpep.nr_sequencia = (select eppac.nr_seq_tipo_episodio
                                                                          from   episodio_paciente eppac
                                                                          where  eppac.nr_seq_tipo_episodio = tpep.nr_sequencia
                                                                          and    eppac.nr_sequencia = nr_sequencia_p));
        end;
    end if;

    return	ds_tipo_admissao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
