CREATE OR REPLACE FUNCTION obter_desc_volume_tratamento ( nr_seq_volume_p bigint) RETURNS varchar AS $body$
DECLARE


ds_volume_w	varchar(90):='';


BEGIN

if (nr_seq_volume_p IS NOT NULL AND nr_seq_volume_p::text <> '') then
	select 	b.ds_volume
	into STRICT	ds_volume_w
	from   	rxt_volume_tratamento a,
		rxt_volume b
	where  	a.nr_seq_volume = b.nr_sequencia
	and	a.nr_sequencia  = nr_seq_volume_p;
end if;

return ds_volume_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
