CREATE OR REPLACE FUNCTION obter_desc_vol_sol_etapa_cpoe (nr_seq_cpoe_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1000);
nr_prescricao_w	prescr_material.nr_prescricao%type;
nr_seq_sol_w	prescr_material.nr_sequencia%type;
ie_acm_w		prescr_material.ie_acm%type;
ie_sn_w			prescr_material.ie_se_necessario%type;


BEGIN

nr_prescricao_w := coalesce(Obter_Nr_Prescr_Seq_Cpoe('SOL', nr_seq_cpoe_p),0);

select 	max(nr_sequencia)
into STRICT	nr_seq_sol_w
from	prescr_material
where	nr_seq_mat_cpoe = nr_seq_cpoe_p;

select 	ie_acm,
		ie_se_necessario
into STRICT	ie_acm_w,
		ie_sn_w
from	prescr_material
where	nr_prescricao = nr_prescricao_w
and		nr_sequencia = nr_seq_sol_w;

ds_retorno_w := obter_desc_vol_etapa_adep2(nr_prescricao_w, nr_seq_sol_w, ie_acm_w, ie_sn_w);

return	substr(ds_retorno_w,1,1000);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
