CREATE OR REPLACE FUNCTION obter_despesa_proc_mat (cd_material_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


retorno_w		varchar(20);

qt_material_w		bigint;
cd_classe_material_w	integer;
cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;


BEGIN

select	count(*)
into STRICT	qt_material_w
from	material m
where	m.cd_material = cd_material_p;

if (qt_material_w > 0) then

	select	v.cd_classe_material,
		v.cd_grupo_material,
		v.cd_subgrupo_material
	into STRICT	cd_classe_material_w,
		cd_grupo_material_w,
		cd_subgrupo_material_w
	from	material m,
		estrutura_material_v v
	where	m.cd_material = v.cd_material
	and	m.cd_material = cd_material_p;

	select	max(y.cd_tipo_despesa) cd_item
	into STRICT	retorno_w
	from	tipo_despesa_mat x,
		tipo_despesa y
	where	x.nr_seq_tipo_despesa	= y.nr_sequencia
	and	((x.cd_material = cd_material_p) or (x.cd_classe_material = cd_classe_material_w) or (x.cd_grupo_material = cd_grupo_material_w) or (x.cd_subgrupo_material = cd_subgrupo_material_w));

else

	select	max(y.cd_tipo_despesa) cd_item
	into STRICT	retorno_w
	from	tipo_despesa_proc x,
		tipo_despesa y
	where	x.nr_seq_tipo_despesa	= y.nr_sequencia
	and	x.cd_procedimento = cd_procedimento_p
	and	x.ie_origem_proced = ie_origem_proced_p;

end if;

return	retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
