CREATE OR REPLACE FUNCTION obter_despesa_viagem_con (nr_seq_proj_p bigint, ie_tipo_p text, dt_mes_inicial_p timestamp, dt_mes_final_p timestamp) RETURNS bigint AS $body$
DECLARE


vl_nossa_empresa_w	double precision;


BEGIN
select	sum(coalesce(c.vl_despesa,0))
into STRICT  	vl_nossa_empresa_w
from	via_destino       d,
	via_despesa       c,
	via_relat_desp    b,
	via_viagem        a
where	a.nr_sequencia    = b.nr_seq_viagem
and	a.nr_sequencia    = d.nr_seq_viagem
and	b.nr_sequencia    = c.nr_seq_relat
and	((d.nr_seq_proj     = nr_seq_proj_p) OR ('0' = nr_seq_proj_p))
and     trunc(b.dt_aprovacao,'month') between trunc(dt_mes_inicial_p,'month') and trunc(dt_mes_final_p,'month')
and	c.ie_responsavel_custo = 'N';

if (ie_tipo_p = 'N') then
return vl_nossa_empresa_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
