CREATE OR REPLACE FUNCTION obter_destino_conjunto (nr_seq_conj_cont_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_retorno_w		varchar(150);
cd_setor_dest_w		integer;
cd_pessoa_fisica_w	varchar(10);
cd_cgc_w		varchar(14);
cd_local_estoque_w	smallint;


BEGIN 
 
select	cd_local_estoque, 
	cd_setor_atend_dest, 
	cd_pessoa_fisica, 
	cd_cgc 
into STRICT	cd_local_estoque_w, 
	cd_setor_dest_w, 
	cd_pessoa_fisica_w, 
	cd_cgc_w 
from	cm_conjunto_cont 
where	nr_sequencia	= nr_seq_conj_cont_p 
and	coalesce(ie_situacao,'A') = 'A';
 
 
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
	select	substr(obter_nome_pessoa_fisica(cd_pessoa_fisica_w, null),1,100) 
	into STRICT	ds_retorno_w 
	;
elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then 
	select	substr(obter_razao_social(cd_cgc_w),1,100) 
	into STRICT	ds_retorno_w 
	;
elsif (cd_setor_dest_w IS NOT NULL AND cd_setor_dest_w::text <> '') then 
	select	substr(obter_nome_setor(cd_setor_dest_w),1,100) 
	into STRICT	ds_retorno_w 
	;
elsif (cd_local_estoque_w IS NOT NULL AND cd_local_estoque_w::text <> '') then 
	select	substr(obter_desc_local_estoque(cd_local_estoque_w),1,100) 
	into STRICT	ds_retorno_w 
	;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
