CREATE OR REPLACE FUNCTION obter_des_exp_pasta (nr_seq_pront_item_pasta_w bigint, ds_rotina_w text) RETURNS varchar AS $body$
DECLARE


	cd_expressao_w bigint;
	ds_expressao_ret varchar(4000) := '';

BEGIN
/*
P = Itens prontu√°rio
E = Itens PEPO
F = Itens FANEP
*/
if ((nr_seq_pront_item_pasta_w IS NOT NULL AND nr_seq_pront_item_pasta_w::text <> '') and (ds_rotina_w IS NOT NULL AND ds_rotina_w::text <> '') and nr_seq_pront_item_pasta_w > 0) then
   if (ds_rotina_w = 'P') then
		select	max(b.cd_exp_desc_pasta)
		into STRICT	cd_expressao_w
		from	prontuario_item_pasta a,
				prontuario_pasta b
		where	a.nr_seq_pasta = b.nr_sequencia
		and		a.nr_sequencia = nr_seq_pront_item_pasta_w;
	end if;

	if (ds_rotina_w = 'E') then
		select	max(b.cd_exp_desc_pasta)
		into STRICT	cd_expressao_w
		from	prontuario_item_pasta a,
				prontuario_pasta b
		where	a.nr_seq_pasta_pepo = b.nr_sequencia
    and		a.nr_seq_pasta_pepo = nr_seq_pront_item_pasta_w;
	end if;

	if (ds_rotina_w = 'F') then
		select	max(b.cd_exp_desc_pasta)
		into STRICT	cd_expressao_w
		from	prontuario_item_pasta a,
				prontuario_pasta b
		where	a.nr_seq_pasta_fanep = b.nr_sequencia
    and		a.nr_seq_pasta_fanep = nr_seq_pront_item_pasta_w;
	end if;

	if ((cd_expressao_w IS NOT NULL AND cd_expressao_w::text <> '') and cd_expressao_w > 0) then
		select	obter_desc_expressao(cd_expressao_w)
		into STRICT	ds_expressao_ret
		;
	end if;

end if;

return ds_expressao_ret;

end;
$body$
