CREATE OR REPLACE FUNCTION obter_des_motivo_urg (cd_motivo_urg_p bigint) RETURNS varchar AS $body$
DECLARE


ds_motivo_w			MOTIVO_URG_LISTA_ESPERA.ds_motivo%type;

c01 CURSOR FOR
	SELECT	ds_motivo
	FROM 	MOTIVO_URG_LISTA_ESPERA
	WHERE 	nr_sequencia = cd_motivo_urg_p
	AND 	(ds_motivo IS NOT NULL AND ds_motivo::text <> '');


BEGIN
ds_motivo_w		:= '';
OPEN C01;
LOOP
FETCH C01 INTO
		ds_motivo_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	ds_motivo_w	:= ds_motivo_w;
END LOOP;
CLOSE C01;
RETURN ds_motivo_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
