SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_des_status_sepse (cd_valor_p text, ie_opcao_p text default 'D') RETURNS varchar AS $body$
DECLARE


ds_descricao_w 		varchar(255) := '';
ie_versao_sepse_w   varchar(10);

/*
ie_opcao_p
D - Descrição
C - Código
*/
BEGIN

select coalesce(ie_versao_sepse,'1')
into STRICT   ie_versao_sepse_w
from   parametro_medico
where  cd_estabelecimento = obter_estabelecimento_ativo;

if (ie_opcao_p = 'C') then

	if (ie_versao_sepse_w = '2') then
		if (cd_valor_p in ('SC', 'RE')) then
			ds_descricao_w := 'SC,RE';
		elsif (cd_valor_p in ('D','RD','RF','SN')) then
			ds_descricao_w := 'D,RD,RF,SN';
		else
			ds_descricao_w := cd_valor_p;
		end if;
	else
		ds_descricao_w := cd_valor_p;
	end if;

else

	if (ie_versao_sepse_w = '2') then
		if (cd_valor_p = 'SM') then
			ds_descricao_w := substr(obter_desc_expressao(881624,'Suspeita de infecção sem disfunção aguardando equipe'),1,255);
		elsif (cd_valor_p = 'SA') then
			ds_descricao_w := substr(obter_desc_expressao(881626,'Suspeita de infecção sem disfunção aguardando ação médica'),1,255);
		elsif (cd_valor_p = 'RN') then
			ds_descricao_w := substr(obter_desc_expressao(301022,'Suspeita de Sepse pendente ação médica'),1,255);
		elsif (cd_valor_p = 'RB') then
			ds_descricao_w := substr(obter_desc_expressao(881628,'Suspeita de Sepse aguardando ação da equipe'),1,255);
		elsif (cd_valor_p in ('SC', 'RE')) then
			ds_descricao_w := substr(obter_desc_expressao(881630,'Infecção sem Disfunção Orgânica'),1,255);
		elsif (cd_valor_p = 'RC') then
			ds_descricao_w := substr(obter_desc_expressao(881826,'Sepse confirmada'),1,255);
		elsif (cd_valor_p in ('D','RD','RF','SN')) then
			ds_descricao_w := substr(obter_desc_expressao(781086,'Sepse descartada'),1,255);
		else
			ds_descricao_w := substr(obter_valor_dominio(7056,cd_valor_p),1,255);
		end if;
	else
		ds_descricao_w := substr(obter_valor_dominio(7056,cd_valor_p),1,255);
	end if;

end if;

return ds_descricao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
