CREATE OR REPLACE FUNCTION obter_diagnosis_principal (nr_atendimento_p atendimento_paciente.nr_atendimento%type, nr_seq_interno_p diagnostico_doenca.nr_seq_interno%type) RETURNS varchar AS $body$
DECLARE

ds_return_w	    cid_doenca.ds_doenca_cid%type;

nr_seq_interno_w  atendimento_paciente_aux.nr_seq_interno%type;
cd_cid_principal_w  atendimento_paciente_aux.cd_cid_principal%type;



BEGIN

if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

select max(apa.cd_cid_principal), max(apa.nr_seq_interno)
into STRICT cd_cid_principal_w,nr_seq_interno_w
from atendimento_paciente_aux apa
where apa.nr_atendimento = nr_atendimento_p;

select max(get_desc_modify_disease_epc(dd.nr_seq_disease_number,dd.ie_side_modifier,dd.nr_seq_jap_pref_1,dd.nr_seq_jap_pref_2,dd.nr_seq_jap_pref_3, dd.nr_seq_jap_sufi_1,dd.nr_seq_jap_sufi_2,dd.nr_seq_jap_sufi_3))
into STRICT ds_return_w
from diagnostico_doenca dd
where dd.nr_atendimento = nr_atendimento_p and dd.nr_seq_interno = nr_seq_interno_w;

if (coalesce(ds_return_w::text, '') = '') then
ds_return_w := obter_desc_cid(cd_cid_principal_w);
end if;

end if;

return ds_return_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
