CREATE OR REPLACE FUNCTION obter_diagnosticos_atendimento ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(4000);
ds_preliminar_w	varchar(4000);
ds_definitivo_w	varchar(4000);
cd_doenca_w	varchar(20);
ds_doenca_w	varchar(2000);
ie_tipo_diagnostico_w varchar(20);
C01 CURSOR FOR
	SELECT	distinct
		b.cd_doenca,
		substr(obter_desc_cid(CD_DOENCA),1,200)
	from	DIAGNOSTICO_MEDICO a,
		DIAGNOSTICO_DOENCA b
	where	a.nr_Atendimento	= b.nr_atendimento
	and	a.dt_diagnostico	= b.dt_diagnostico
	and	a.nr_atendimento	= nr_atendimento_p;

BEGIN

begin
open C01;
loop
fetch C01 into
	cd_doenca_w,
	ds_doenca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:=  cd_doenca_w || ' - '|| ds_doenca_w;

	else
		ds_retorno_w	:= ds_retorno_w||', '||cd_doenca_w || ' - '|| ds_doenca_w;
	end if;

	end;
end loop;
close C01;

if (ds_preliminar_w IS NOT NULL AND ds_preliminar_w::text <> '') then
	ds_retorno_w	:= ds_preliminar_w|| chr(13)||chr(10);
end if;

if (ds_definitivo_w IS NOT NULL AND ds_definitivo_w::text <> '') then
	ds_retorno_w	:=ds_retorno_w||ds_definitivo_w;
end if;
exception
	when others then

	null;

end;
return	ds_retorno_w;

end;
$body$
