CREATE OR REPLACE FUNCTION obter_diagnosticos_ativos (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w varchar(4000);
cd_doenca_w	diagnostico_doenca.cd_doenca%type;

C01 CURSOR FOR
	SELECT	distinct b.cd_doenca
	from    DIAGNOSTICO_MEDICO a,
                DIAGNOSTICO_DOENCA b
	where   a.nr_Atendimento	= b.nr_atendimento
	and	a.dt_diagnostico	= b.dt_diagnostico
	and     a.nr_atendimento	= nr_atendimento_p
        and     coalesce(b.dt_inativacao::text, '') = ''
        and     (b.dt_liberacao IS NOT NULL AND b.dt_liberacao::text <> '');

BEGIN

begin
open C01;
loop
fetch C01 into
	cd_doenca_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
    if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:=  cd_doenca_w;
	else
		ds_retorno_w	:= ds_retorno_w||', '||cd_doenca_w;
	end if;

	end;
end loop;
close C01;

end;
return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
