SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_diagnosticos_concat (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_diagnostico_w	varchar(2000);
ds_diag_w		varchar(200);

C01 CURSOR FOR
	SELECT distinct substr(obter_desc_cid(cd_doenca),1,200) ds_diagnostico
	from diagnostico_doenca
	where nr_atendimento = nr_atendimento_p
	and ie_situacao = 'A';

BEGIN

open C01;
loop
fetch C01 into
	ds_diag_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(ds_diagnostico_w::text, '') = '') then
		ds_diagnostico_w := trim(both ds_diag_w);
	else
		ds_diagnostico_w := ds_diagnostico_w || ', ' || trim(both ds_diag_w);
	end if;
	end;
end loop;
close C01;


return	ds_diagnostico_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
