CREATE OR REPLACE FUNCTION obter_diagnostico_eup (nr_atendimento_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE

cd_doenca_w	varchar(10);

BEGIN

if (coalesce(nr_atendimento_p,0) > 0) then

	if (ie_tipo_p = 'P') then

		select	max(cd_doenca)
		into STRICT	cd_doenca_w
		from	diagnostico_doenca
		where 	nr_atendimento = nr_atendimento_p
		and  	ie_classificacao_doenca = ie_tipo_p;

	elsif (ie_tipo_p = 'S') then

		select	max(cd_doenca)
		into STRICT	cd_doenca_w
		from  	diagnostico_doenca
		where 	nr_atendimento = nr_atendimento_p
		and     ie_classificacao_doenca = ie_tipo_p;
	end if;
end if;

return	cd_doenca_w;

end;
