CREATE OR REPLACE FUNCTION obter_diagnost_doenca_nom (nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w    varchar(2000);


BEGIN
  ds_retorno_w := '';

  if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
    select  upper(max(cd.ds_doenca_cid))
    into STRICT ds_retorno_w
    from diagnostico_doenca dd, cid_doenca cd
    where dd.nr_atendimento = nr_atendimento_p
    and dd.cd_doenca = cd.cd_doenca_cid
    and dd.ie_classificacao_doenca = 'P';

  end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
