SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_diag_atend_opcao (nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
D - data diagnóstico
M - médico diagnóstico
CM - código médico diagnóstico
C - cid diagnóstico
CD - código cid diagnóstico
*/
dt_diagnostico_w	timestamp;
ds_retorno_w		varchar(255);


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	select	max(a.dt_diagnostico)
	into STRICT	dt_diagnostico_w
	from	diagnostico_doenca b,
		diagnostico_medico a
	where	b.dt_diagnostico = a.dt_diagnostico
	and	b.nr_atendimento = a.nr_atendimento
	and	a.nr_atendimento = nr_atendimento_p;

	if (dt_diagnostico_w IS NOT NULL AND dt_diagnostico_w::text <> '') then
		if (coalesce(ie_opcao_p,'D') = 'D') then
			ds_retorno_w	:= to_char(dt_diagnostico_w,'dd/mm/yyyy hh24:mi:ss');

		elsif (coalesce(ie_opcao_p,'D') = 'M') then
			select	substr(obter_nome_pf(coalesce(max(a.cd_medico),'0')),1,60)
			into STRICT	ds_retorno_w
			from	diagnostico_doenca b,
				diagnostico_medico a
			where	b.dt_diagnostico = a.dt_diagnostico
			and	b.nr_atendimento = a.nr_atendimento
			and	a.dt_diagnostico = dt_diagnostico_w
			and	a.nr_atendimento = nr_atendimento_p;

		elsif (coalesce(ie_opcao_p,'D') = 'CM') then
			select	coalesce(max(a.cd_medico),'0')
			into STRICT	ds_retorno_w
			from	diagnostico_doenca b,
				diagnostico_medico a
			where	b.dt_diagnostico = a.dt_diagnostico
			and	b.nr_atendimento = a.nr_atendimento
			and	a.dt_diagnostico = dt_diagnostico_w
			and	a.nr_atendimento = nr_atendimento_p;

		elsif (coalesce(ie_opcao_p,'D') = 'C') then
			select	substr(obter_desc_cid_doenca(coalesce(max(cd_doenca),'0')),1,240)
			into STRICT	ds_retorno_w
			from	diagnostico_doenca
			where	dt_diagnostico = dt_diagnostico_w
			and	nr_atendimento = nr_atendimento_p;

		elsif (coalesce(ie_opcao_p,'D') = 'CD') then
			select	coalesce(max(cd_doenca),'0')
			into STRICT	ds_retorno_w
			from	diagnostico_doenca
			where	dt_diagnostico = dt_diagnostico_w
			and	nr_atendimento = nr_atendimento_p;
		end if;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
