SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_diag_filtro_rel (ds_filtro_p text, ie_campo_p text ) RETURNS varchar AS $body$
DECLARE


ds_filtro_w  varchar(4000) := '';
qt_tamanho_w bigint;
qt_inicial_w bigint;
qt_final_w   bigint;


BEGIN

select length(ds_filtro_p)
into STRICT   qt_tamanho_w
;

if (substr(ds_filtro_p,6,1) = 'w') then
	select position(ie_campo_p in ds_filtro_p) + 1
	into STRICT   qt_inicial_w
	;

	select substr(ds_filtro_p,qt_inicial_w,qt_tamanho_w)
	into STRICT  ds_filtro_w
	;

elsif (coalesce(position('w' in ds_filtro_p),0) > 0) then
		select position('w' in ds_filtro_p)
		into STRICT   qt_final_w
		;

		select substr(ds_filtro_p,1,qt_final_w - 5)
		into STRICT   ds_filtro_w
		;

		if (qt_final_w = 0) then
			select position(')' in ds_filtro_p)
			into STRICT   qt_final_w
			;

			select substr(ds_filtro_p,1,qt_final_w)
			into STRICT   ds_filtro_w
			;
		end if;

else
	select length(ds_filtro_p)
	into STRICT   qt_tamanho_w
	;

	select position('(' in ds_filtro_p) + 1
	into STRICT   qt_inicial_w
	;

	qt_inicial_w := 1;

	if (qt_inicial_w > 0) then
		select substr(ds_filtro_p,qt_inicial_w,qt_tamanho_w)
		into STRICT  ds_filtro_w
		;
	end if;

end if;

return	ds_filtro_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
