CREATE OR REPLACE FUNCTION obter_dialysis_event ( nr_atendimento_p bigint ) RETURNS varchar AS $body$
DECLARE

ds_evento_w	varchar(255);
  /*

  Tipo de dialysis:
  II = Infusion started
  DPAI = Automated start
  */
  ie_evento_w  varchar(15);
  c01 CURSOR FOR
    SELECT max(e.ie_evento)
	from hd_prescricao_evento e,
		prescr_solucao x,
		prescr_medica a
	where a.nr_atendimento= nr_atendimento_p
		and x.nr_prescricao = a.nr_prescricao
		and  e.nr_prescricao = a.nr_prescricao
        and coalesce(e.ie_evento_valido,'S') = 'S'
        order by e.dt_evento desc;


BEGIN

    OPEN C01;
    LOOP
      FETCH C01 INTO  ie_evento_w;
      EXIT WHEN NOT FOUND; /* apply on C01 */
      IF (ie_evento_w in ('II','DPAI')) THEN
        ds_evento_w   := obter_desc_expressao(928855);
      else
        ds_evento_w  :=obter_desc_expressao(928857);

      END IF;
    END LOOP;
    CLOSE C01;
RETURN ds_evento_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
