CREATE OR REPLACE FUNCTION obter_diarias_setor_mes ( cd_setor_atendimento_p bigint, nr_atendimento_p bigint, dt_mes_p text) RETURNS bigint AS $body$
DECLARE


qt_total_dias_w	bigint;


BEGIN

	SELECT	SUM(qt_total_c)
	INTO STRICT	qt_total_dias_w
	FROM
	(
	SELECT	DISTINCT
		obter_dias_entre_datas(dt_entrada_unidade,dt_saida_unidade) qt_total_c
	FROM	atend_paciente_unidade
	WHERE	((TO_CHAR(dt_entrada_unidade,'mm/yyyy') = dt_mes_p)
		OR (TO_CHAR(dt_saida_unidade,'mm/yyyy') = dt_mes_p)
		OR (TO_CHAR(dt_entrada_unidade,'mm/yyyy') < dt_mes_p AND coalesce(dt_saida_unidade::text, '') = ''))
	AND	cd_setor_atendimento = cd_setor_atendimento_p
	and	nr_atendimento = nr_atendimento_p
	GROUP BY 	nr_atendimento, dt_entrada_unidade, dt_saida_unidade
	) alias10;

return coalesce(qt_total_dias_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
