CREATE OR REPLACE FUNCTION obter_dias_aplic_recomend (nr_seq_paciente_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	 	varchar(4000);
qt_tam_w	 	bigint;
ds_dias_aplica_w 	varchar(4000);
ds_dias_aplica_soluc_w	varchar(4000);


BEGIN

select 	max(length(ds_dias_aplicacao))
into STRICT	qt_tam_w
from   	paciente_protocolo_medic
where  	nr_seq_paciente = nr_seq_paciente_p;

select 	max(ds_dias_aplicacao)
into STRICT	ds_dias_aplica_w
from   	paciente_protocolo_medic
where  	nr_seq_paciente = nr_seq_paciente_p
and	length(ds_dias_aplicacao) = qt_tam_w;


select 	max(length(ds_dias_aplicacao))
into STRICT	qt_tam_w
from   	paciente_protocolo_soluc
where  	nr_seq_paciente = nr_seq_paciente_p;

select 	max(ds_dias_aplicacao)
into STRICT	ds_dias_aplica_soluc_w
from   	paciente_protocolo_soluc
where  	nr_seq_paciente = nr_seq_paciente_p
and	length(ds_dias_aplicacao) = qt_tam_w;

if (length(ds_dias_aplica_soluc_w) > length(ds_dias_aplica_w)) then
	ds_retorno_w	:= ds_dias_aplica_soluc_w;
else
	ds_retorno_w	:= ds_dias_aplica_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
