CREATE OR REPLACE FUNCTION obter_dias_atraso_autor (nr_sequencia_autor_p bigint) RETURNS bigint AS $body$
DECLARE


qt_dias_atraso_w	bigint;


BEGIN

if (nr_sequencia_autor_p IS NOT NULL AND nr_sequencia_autor_p::text <> '') then
	select 	round(clock_timestamp() - (dt_envio + coalesce(qt_dias_prazo,0)),0)
	into STRICT 	qt_dias_atraso_w
	from 	autorizacao_convenio
	where 	nr_sequencia = nr_sequencia_autor_p;

	if (qt_dias_atraso_w > 0) then
		qt_dias_atraso_w	:= qt_dias_atraso_w;
	else
		qt_dias_atraso_w	:= 0;
	end if;

end if;

return	qt_dias_atraso_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
