SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dias_atraso_tit_cheque ( nr_titulo_p bigint, nr_seq_cheque_p bigint) RETURNS bigint AS $body$
DECLARE


dt_vencimento_w		timestamp;
qt_dias_atraso_w	bigint	:= 0;


BEGIN

if (nr_titulo_p IS NOT NULL AND nr_titulo_p::text <> '') then

	select	a.dt_pagamento_previsto
	into STRICT	dt_vencimento_w
	from	titulo_receber a
	where	a.nr_titulo	= nr_titulo_p;

elsif (nr_seq_cheque_p IS NOT NULL AND nr_seq_cheque_p::text <> '') then

	select	coalesce(a.dt_vencimento_atual,a.dt_vencimento)
	into STRICT	dt_vencimento_w
	from	cheque_cr a
	where	a.nr_seq_cheque	= nr_seq_cheque_p;
end if;

if (dt_vencimento_w < trunc(clock_timestamp(),'dd')) then
	qt_dias_atraso_w	:=	trunc(clock_timestamp(),'dd') - dt_vencimento_w;
end if;

return	qt_dias_atraso_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
