CREATE OR REPLACE FUNCTION obter_dias_ausencia_usuario ( dt_atividade_inicio_p timestamp, dt_atividade_fim_p timestamp, cd_motivo_ausencia_p bigint, nm_usuario_p text) RETURNS bigint AS $body$
DECLARE


qt_dias_ausencia_w	bigint;			


BEGIN

select	sum(a.qt_horas_absent) qt_dias_ausencia
into STRICT	qt_dias_ausencia_w
from (SELECT	case
		when(x.dt_inicio between dt_atividade_inicio_p and dt_atividade_fim_p) and x.dt_fim >= dt_atividade_fim_p then
			OBTER_DIAS_UTEIS_PERIODO(x.dt_inicio,dt_atividade_fim_p,y.cd_estabelecimento)
		when(x.dt_inicio between dt_atividade_inicio_p and dt_atividade_fim_p) and x.dt_fim < dt_atividade_fim_p then
			OBTER_DIAS_UTEIS_PERIODO(x.dt_inicio,x.dt_fim,y.cd_estabelecimento)
		when(x.dt_fim between dt_atividade_inicio_p and dt_atividade_fim_p) then
			OBTER_DIAS_UTEIS_PERIODO(dt_atividade_inicio_p,x.dt_fim,y.cd_estabelecimento)
		else 0
		end qt_horas_absent
	from 	ausencia_tasy x,
			usuario y
	where 	x.nm_usuario_ausente = nm_usuario_p
	and		y.nm_usuario = x.nm_usuario_ausente
	and   	x.cd_motivo_ausencia = cd_motivo_ausencia_p
	and (x.dt_inicio between dt_atividade_inicio_p and dt_atividade_fim_p or
		 x.dt_fim between dt_atividade_inicio_p and dt_atividade_fim_p)
	
union all

	SELECT	0 qt_horas_absent
	
	where  	not exists (	select	1
				from   	AUSENCIA_TASY b
				where  	b.nm_usuario_ausente = nm_usuario_p
				and    	b.cd_motivo_ausencia = cd_motivo_ausencia_p
				and (b.dt_inicio between dt_atividade_inicio_p and dt_atividade_fim_p or
					 b.dt_fim between dt_atividade_inicio_p and dt_atividade_fim_p))) a;


return	qt_dias_ausencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
