CREATE OR REPLACE FUNCTION obter_dias_entrega_exame_lab ( nr_prescricao_p bigint, nr_seq_exame_p bigint, cd_setor_solicitacao_p bigint, cd_material_exame_p text, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

 
ie_tipo_atendimento_w	smallint;
dt_entrada_w		timestamp;
nr_seq_material_w	bigint;

nr_seq_grupo_w		bigint;
cd_estabelecimento_w	smallint;
qt_dia_entrega_w	smallint;

ds_retorno_w		varchar(50);
c01 CURSOR FOR
	SELECT	coalesce(qt_dia_entrega,0) 
	from exame_lab_regra_setor 
	where ((coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w) = ie_tipo_atendimento_w ) or (coalesce(ie_tipo_atendimento_w::text, '') = '')) 
	 and ((coalesce(cd_setor_solicitacao, cd_setor_solicitacao_p) = cd_setor_solicitacao_p) or (coalesce(cd_setor_solicitacao_p::text, '') = '')) 
	 and coalesce(nr_seq_grupo, nr_seq_grupo_w) = nr_seq_grupo_w 
	 and coalesce(nr_seq_exame, nr_seq_exame_p) = nr_seq_exame_p 
	 and ((coalesce(nr_seq_material, nr_seq_material_w) = nr_seq_material_w) or (coalesce(nr_seq_material_w::text, '') = '')) 
	order by coalesce(nr_seq_prioridade,0),coalesce(nr_seq_material,0), coalesce(nr_seq_exame,0), coalesce(nr_seq_grupo,0);


BEGIN 
 
select	max(coalesce(b.cd_estabelecimento,a.cd_estabelecimento)), 
	max(b.ie_tipo_atendimento) 
into STRICT	cd_estabelecimento_w, 
	ie_tipo_atendimento_w 
FROM prescr_medica a
LEFT OUTER JOIN atendimento_paciente b ON (a.nr_atendimento = b.nr_atendimento)
WHERE a.nr_prescricao	= nr_prescricao_p;
 
qt_dia_entrega_w		:= 0;
 
select max(nr_seq_grupo) 
into STRICT nr_seq_grupo_w 
from exame_laboratorio 
where nr_seq_exame = nr_seq_exame_p;
 
select 	max(nr_sequencia) 
into STRICT	nr_seq_material_w 
from 	material_exame_lab 
where cd_material_exame = cd_material_exame_p;
 
open c01;
loop 
	fetch c01 into	qt_dia_entrega_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
 
end loop;
close c01;
 
ds_retorno_w := qt_dia_entrega_w;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
