SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dias_entrega_item_contr ( cd_material_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_dias_entrega_w		integer;
nr_seq_contrato_w		bigint;
ie_estoque_w			varchar(1);


BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	select (max(obter_valor_param_usuario(913, 185, wheb_usuario_pck.get_cd_perfil, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento)))
	into STRICT	ie_estoque_w
	;

	select	coalesce(max(nr_seq_contrato),0)
	into STRICT	nr_seq_contrato_w
	from   (SELECT	a.nr_Seq_contrato
		from	contrato_regra_nf a,
			contrato b
		where	a.nr_seq_contrato = b.nr_sequencia
		and	(((ie_estoque_w = 'N') and (coalesce(a.cd_material, cd_material_p) = cd_material_p)) or
			((ie_estoque_w = 'S') and (obter_dados_material(a.cd_material,'EST') = obter_dados_material(cd_material_p,'EST'))))
		and	substr(obter_dados_pf_pj(null,b.cd_cgc_contratado,'S'),1,1) = 'A'
		and	((coalesce(a.dt_inicio_vigencia::text, '') = '') or (trunc(a.dt_inicio_vigencia,'dd') <= trunc(clock_timestamp(),'dd')))
		and	((coalesce(a.dt_fim_vigencia::text, '') = '') or (trunc(a.dt_fim_vigencia,'dd') >= trunc(clock_timestamp(),'dd')))
        and (coalesce(a.ie_situacao::text, '') = '' or a.ie_situacao = 'A')
		and	coalesce(cd_pessoa_contratada::text, '') = ''
		and	coalesce(b.ie_situacao,'A') = 'A'
		
union all

		SELECT	a.nr_seq_contrato
		from	contrato_regra_nf a,
			contrato b
		where	a.nr_seq_contrato = b.nr_sequencia
		and	(((ie_estoque_w = 'N') and (coalesce(a.cd_material, cd_material_p) = cd_material_p)) or
			((ie_estoque_w = 'S') and (obter_dados_material(a.cd_material,'EST') = obter_dados_material(cd_material_p,'EST'))))
		and	((coalesce(a.dt_inicio_vigencia::text, '') = '') or (trunc(a.dt_inicio_vigencia,'dd') <= trunc(clock_timestamp(),'dd')))
		and	((coalesce(a.dt_fim_vigencia::text, '') = '') or (trunc(a.dt_fim_vigencia,'dd') >= trunc(clock_timestamp(),'dd')))
        and (coalesce(a.ie_situacao::text, '') = '' or a.ie_situacao = 'A')
		and	(cd_pessoa_contratada IS NOT NULL AND cd_pessoa_contratada::text <> '')
		and	coalesce(b.ie_situacao,'A') = 'A') alias60;

		if (nr_seq_contrato_w > 0) then
		
			select	coalesce(max(qt_dias_entrega),0)
			into STRICT	qt_dias_entrega_w
			from	contrato_estab_adic
			where	nr_seq_contrato = nr_seq_contrato_w
			and	cd_estab_adic = cd_estabelecimento_p;
			
			if (qt_dias_entrega_w = 0) then
				
				select	max(qt_dias_entrega)
				into STRICT	qt_dias_entrega_w
				from   (SELECT	a.qt_dias_entrega
					from	contrato_regra_nf a,
						contrato b
					where	a.nr_seq_contrato = b.nr_sequencia
					and	(((ie_estoque_w = 'N') and (coalesce(a.cd_material, cd_material_p) = cd_material_p)) or
						((ie_estoque_w = 'S') and (obter_dados_material(a.cd_material,'EST') = obter_dados_material(cd_material_p,'EST'))))
					and	substr(obter_dados_pf_pj(null,b.cd_cgc_contratado,'S'),1,1) = 'A'
					and	((coalesce(a.dt_inicio_vigencia::text, '') = '') or (trunc(a.dt_inicio_vigencia,'dd') <= trunc(clock_timestamp(),'dd')))
					and	((coalesce(a.dt_fim_vigencia::text, '') = '') or (trunc(a.dt_fim_vigencia,'dd') >= trunc(clock_timestamp(),'dd')))
                    and (coalesce(a.ie_situacao::text, '') = '' or a.ie_situacao = 'A')
					and	coalesce(cd_pessoa_contratada::text, '') = ''
					and	coalesce(b.ie_situacao,'A') = 'A'
					
union all

					SELECT	a.qt_dias_entrega
					from	contrato_regra_nf a,
						contrato b
					where	a.nr_seq_contrato = b.nr_sequencia
					and	(((ie_estoque_w = 'N') and (coalesce(a.cd_material, cd_material_p) = cd_material_p)) or
						((ie_estoque_w = 'S') and (obter_dados_material(a.cd_material,'EST') = obter_dados_material(cd_material_p,'EST'))))
					and	((coalesce(a.dt_inicio_vigencia::text, '') = '') or (trunc(a.dt_inicio_vigencia,'dd') <= trunc(clock_timestamp(),'dd')))
					and	((coalesce(a.dt_fim_vigencia::text, '') = '') or (trunc(a.dt_fim_vigencia,'dd') >= trunc(clock_timestamp(),'dd')))
                    and (coalesce(a.ie_situacao::text, '') = '' or a.ie_situacao = 'A')
					and	(cd_pessoa_contratada IS NOT NULL AND cd_pessoa_contratada::text <> '')
					and	coalesce(b.ie_situacao,'A') = 'A') alias60;
			
			end if;		
		end if;	
end if;

return	qt_dias_entrega_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
